package email

import "github.com/jjwoz/onQueue/internal/util"

/*
RegistrationWelcomeSend send registration email to designated email address.
*/
func RegistrationWelcomeSend(emailAddress string) {
	mail := util.Mail{
		From:     "no-reply@help.io",
		To:       []string{emailAddress},
		Subject:  util.SetSubject("Marry", "LIFE"),
		BodyType: "text/html",
		Body:     registrationMailBody(),
		//Attachment: []string{"img.jpg", "img.jpg"},
		Attachment: nil,
	}

	util.SendMail(mail)
}

